return;
}
RulesModule rulesSource = (RulesModule) cls.newInstance();
install(rulesSource);
} catch (Exception e) {
targetRulesBinder.addError("Impossible to include programmatic rules from class '%s': %s", className, e.getMessage());
}
}
}

